import React, { useState, useEffect } from "react";
import axios from "axios";
import { Navbar, Nav, Container, Button, Carousel, Card } from "react-bootstrap";

function App() {
  // States
  const [donations, setDonations] = useState([]);
  const [donationForm, setDonationForm] = useState({ donorName: "", foodType: "", quantity: "", location: "" });
  const [receiverForm, setReceiverForm] = useState({ receiverName: "", contact: "", location: "", requiredFood: "" });
  const [volunteerForm, setVolunteerForm] = useState({ volunteerName: "", contact: "", area: "", availability: "" });

  // Fetch donations initially
  useEffect(() => {
    axios.get("http://localhost:5000/donations").then(res => setDonations(res.data));
  }, []);

  // Handle Donations
  const handleDonationSubmit = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:5000/donations", donationForm);
    setDonationForm({ donorName: "", foodType: "", quantity: "", location: "" });
    const res = await axios.get("http://localhost:5000/donations");
    setDonations(res.data);
  };

  // Handle Receivers
  const handleReceiverSubmit = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:5000/receivers", receiverForm);
    setReceiverForm({ receiverName: "", contact: "", location: "", requiredFood: "" });
    alert("‚úÖ Food request submitted!");
  };

  // Handle Volunteers
  const handleVolunteerSubmit = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:5000/volunteers", volunteerForm);
    setVolunteerForm({ volunteerName: "", contact: "", area: "", availability: "" });
    alert("üôå Thank you for registering as a Volunteer!");
  };

  return (
    <>
      {/* Navbar */}
      <Navbar bg="dark" variant="dark" expand="lg" sticky="top">
        <Container>
          <Navbar.Brand href="#">üç¥ FoodSaver</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="#home">Home</Nav.Link>
              <Nav.Link href="#donate">Donate</Nav.Link>
              <Nav.Link href="#request">Request For Food</Nav.Link>
              <Nav.Link href="#volunteer">Volunteers</Nav.Link>
              <Nav.Link href="#contact">Contact Us</Nav.Link>
            </Nav>
            <Button variant="outline-light" className="me-2">Profile</Button>
            <Button variant="danger">Logout</Button>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {/* Hero Carousel */}
      <Carousel id="home">
        <Carousel.Item>
          <img className="d-block w-100" src="https://source.unsplash.com/1600x600/?food,donation" alt="First slide" />
          <Carousel.Caption>
            <h3>OUR MISSION</h3>
            <p>Make India Hunger Free</p>
          </Carousel.Caption>
        </Carousel.Item>
        <Carousel.Item>
          <img className="d-block w-100" src="https://source.unsplash.com/1600x600/?charity,help" alt="Second slide" />
          <Carousel.Caption>
            <h3>Contribute Today</h3>
            <p>Your small help can change lives.</p>
          </Carousel.Caption>
        </Carousel.Item>
      </Carousel>

      {/* Donation Form */}
      <Container className="mt-5" id="donate">
        <h2 className="text-center mb-4">üç≤ Donate Food</h2>
        <form onSubmit={handleDonationSubmit} className="p-4 bg-light rounded shadow">
          <input className="form-control mb-2" placeholder="Donor Name" value={donationForm.donorName} onChange={e => setDonationForm({ ...donationForm, donorName: e.target.value })} />
          <input className="form-control mb-2" placeholder="Food Type" value={donationForm.foodType} onChange={e => setDonationForm({ ...donationForm, foodType: e.target.value })} />
          <input className="form-control mb-2" placeholder="Quantity" value={donationForm.quantity} onChange={e => setDonationForm({ ...donationForm, quantity: e.target.value })} />
          <input className="form-control mb-2" placeholder="Location" value={donationForm.location} onChange={e => setDonationForm({ ...donationForm, location: e.target.value })} />
          <Button type="submit" variant="success" className="w-100">Donate</Button>
        </form>
      </Container>

      {/* Display Donations */}
      <Container className="mt-5">
        <h3 className="text-center mb-4">üìã Recent Donations</h3>
        <div className="d-flex flex-wrap gap-3 justify-content-center">
          {donations.map(d => (
            <Card key={d._id} style={{ width: "18rem" }} className="shadow">
              <Card.Body>
                <Card.Title>{d.donorName}</Card.Title>
                <Card.Text>
                  {d.quantity} of {d.foodType}<br />
                  üìç {d.location}<br />
                  Status: {d.status}
                </Card.Text>
              </Card.Body>
            </Card>
          ))}
        </div>
      </Container>

      {/* Receiver Form */}
      <Container className="mt-5" id="request">
        <h2 className="text-center mb-4">üôè Request Food</h2>
        <form onSubmit={handleReceiverSubmit} className="p-4 bg-light rounded shadow">
          <input className="form-control mb-2" placeholder="Receiver Name" value={receiverForm.receiverName} onChange={e => setReceiverForm({ ...receiverForm, receiverName: e.target.value })} />
          <input className="form-control mb-2" placeholder="Contact" value={receiverForm.contact} onChange={e => setReceiverForm({ ...receiverForm, contact: e.target.value })} />
          <input className="form-control mb-2" placeholder="Location" value={receiverForm.location} onChange={e => setReceiverForm({ ...receiverForm, location: e.target.value })} />
          <input className="form-control mb-2" placeholder="Required Food" value={receiverForm.requiredFood} onChange={e => setReceiverForm({ ...receiverForm, requiredFood: e.target.value })} />
          <Button type="submit" variant="primary" className="w-100">Submit Request</Button>
        </form>
      </Container>

      {/* Volunteer Form */}
      <Container className="mt-5" id="volunteer">
        <h2 className="text-center mb-4">üôå Become a Volunteer</h2>
        <form onSubmit={handleVolunteerSubmit} className="p-4 bg-light rounded shadow">
          <input className="form-control mb-2" placeholder="Volunteer Name" value={volunteerForm.volunteerName} onChange={e => setVolunteerForm({ ...volunteerForm, volunteerName: e.target.value })} />
          <input className="form-control mb-2" placeholder="Contact" value={volunteerForm.contact} onChange={e => setVolunteerForm({ ...volunteerForm, contact: e.target.value })} />
          <input className="form-control mb-2" placeholder="Area" value={volunteerForm.area} onChange={e => setVolunteerForm({ ...volunteerForm, area: e.target.value })} />
          <input className="form-control mb-2" placeholder="Availability" value={volunteerForm.availability} onChange={e => setVolunteerForm({ ...volunteerForm, availability: e.target.value })} />
          <Button type="submit" variant="warning" className="w-100">Register</Button>
        </form>
      </Container>
    </>
  );
}

export default App;
