import React, { useState, useEffect } from "react";
import axios from "axios";
import { Navbar, Nav, Container, Button, Carousel, Card } from "react-bootstrap";

function App() {
  const [donations, setDonations] = useState([]);
  const [form, setForm] = useState({
    donorName: "",
    foodType: "",
    quantity: "",
    location: "",
  });

  useEffect(() => {
    fetchDonations();
  }, []);

  const fetchDonations = async () => {
    try {
      const res = await axios.get("http://localhost:5000/donations");
      setDonations(res.data);
    } catch (err) {
      console.error("Error fetching donations:", err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/donations", form);
      setForm({ donorName: "", foodType: "", quantity: "", location: "" });
      fetchDonations();
    } catch (err) {
      console.error("Error submitting donation:", err);
    }
  };

  return (
    <>
      {/* Navbar */}
      <Navbar bg="dark" variant="dark" expand="lg" sticky="top">
        <Container>
          <Navbar.Brand href="#">üç¥ FoodSaver</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="#">Home</Nav.Link>
              <Nav.Link href="#donate">Donate</Nav.Link>
              <Nav.Link href="#request">Request For Food</Nav.Link>
              <Nav.Link href="#contributors">Contributors</Nav.Link>
              <Nav.Link href="#contact">Contact Us</Nav.Link>
            </Nav>
            <Button variant="outline-light" className="me-2">
              Profile
            </Button>
            <Button variant="danger">Logout</Button>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {/* Image Banner */}
      <div>
        <img
          src="https://source.unsplash.com/1600x400/?food,community"
          alt="Food Banner"
          className="w-100"
        />
      </div>

      {/* Hero Carousel */}
      <Carousel>
        <Carousel.Item>
          <img
            className="d-block w-100"
            src="https://source.unsplash.com/1600x600/?food,donation"
            alt="First slide"
          />
          <Carousel.Caption>
            <h3>OUR MISSION</h3>
            <p>Make India Hunger Free</p>
          </Carousel.Caption>
        </Carousel.Item>
        <Carousel.Item>
          <img
            className="d-block w-100"
            src="https://source.unsplash.com/1600x600/?charity,help"
            alt="Second slide"
          />
          <Carousel.Caption>
            <h3>Contribute Today</h3>
            <p>Your small help can change lives.</p>
          </Carousel.Caption>
        </Carousel.Item>
      </Carousel>

      {/* Donation Form */}
      <Container className="mt-5" id="donate">
        <h2 className="text-center mb-4">üç≤ Donate Food</h2>
        <form onSubmit={handleSubmit} className="p-4 bg-light rounded shadow">
          <input
            className="form-control mb-2"
            placeholder="Donor Name"
            value={form.donorName}
            onChange={(e) => setForm({ ...form, donorName: e.target.value })}
          />
          <input
            className="form-control mb-2"
            placeholder="Food Type"
            value={form.foodType}
            onChange={(e) => setForm({ ...form, foodType: e.target.value })}
          />
          <input
            className="form-control mb-2"
            placeholder="Quantity"
            value={form.quantity}
            onChange={(e) => setForm({ ...form, quantity: e.target.value })}
          />
          <input
            className="form-control mb-2"
            placeholder="Location"
            value={form.location}
            onChange={(e) => setForm({ ...form, location: e.target.value })}
          />
          <Button type="submit" variant="success" className="w-100">
            Donate
          </Button>
        </form>
      </Container>

      {/* Display Donations */}
      <Container className="mt-5">
        <h3 className="text-center mb-4">üìã Recent Donations</h3>
        <div className="d-flex flex-wrap gap-3 justify-content-center">
          {donations.length > 0 ? (
            donations.map((d) => (
              <Card key={d._id} style={{ width: "18rem" }} className="shadow">
                <Card.Body>
                  <Card.Title>{d.donorName}</Card.Title>
                  <Card.Text>
                    {d.quantity} of {d.foodType}
                    <br />
                    üìç {d.location}
                    <br />
                    Status: {d.status || "Pending"}
                  </Card.Text>
                </Card.Body>
              </Card>
            ))
          ) : (
            <p className="text-muted">No donations yet.</p>
          )}
        </div>
      </Container>

      {/* Request for Food Section */}
      <Container className="mt-5" id="request">
        <h3 className="text-center mb-4">üôè Request For Food</h3>
        <p className="text-center text-muted">
          This section will allow users to request food in future updates.
        </p>
      </Container>

      {/* Contributors Section */}
      <Container className="mt-5" id="contributors">
        <h3 className="text-center mb-4">üôå Contributors</h3>
        <p className="text-center text-muted">
          Coming soon... Showcase of all amazing contributors.
        </p>
      </Container>

      {/* Contact Section */}
      <Container className="mt-5 mb-5" id="contact">
        <h3 className="text-center mb-4">üìû Contact Us</h3>
        <p className="text-center">Email: support@foodsaver.org | Phone: +91 98765 43210</p>
      </Container>
    </>
  );
}

export default App;
