import React, { useState, useEffect } from "react";
import axios from "axios";
import { Navbar, Nav, Container, Button, Card, Row, Col } from "react-bootstrap";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import "./App.css";

// -------------------- HOME PAGE --------------------
function Home() {
  const blogs = [
    { title: "NGOs Supporting Hunger", text: "Partner with NGOs to distribute food.", img: "/images/ngo.jpg" },
    { title: "Orphan Homes", text: "Provide meals to orphanages and children in need.", img: "/images/orphan.jpg" },
    { title: "Community Support", text: "Build local communities to fight hunger together.", img: "/images/community.jpeg" }
  ];

  return (
    <Container className="mt-5">
      <h1 className="text-center mb-4">Welcome to Food Saver</h1>
      <p className="text-center mb-5">Join us in eliminating hunger and saving food.</p>
      <Row className="g-4">
        {blogs.map((b, i) => (
          <Col md={4} key={i}>
            <Card className="h-100 shadow">
              <Card.Img variant="top" src={b.img} />
              <Card.Body>
                <Card.Title>{b.title}</Card.Title>
                <Card.Text>{b.text}</Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

// -------------------- DONATE PAGE --------------------
function Donate() {
  const [donations, setDonations] = useState([]);
  const [form, setForm] = useState({ donorName: "", foodType: "", quantity: "", location: "" });

  useEffect(() => { fetchDonations(); }, []);

  const fetchDonations = async () => {
    try { const res = await axios.get("http://localhost:5000/donations"); setDonations(res.data); }
    catch (err) { console.error(err); }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/donations", form);
      setForm({ donorName: "", foodType: "", quantity: "", location: "" });
      fetchDonations();
    } catch (err) { console.error(err); }
  };

  return (
    <Container className="mt-5">
      <h2 className="text-center mb-4">🍲 Donate Food</h2>
      <form onSubmit={handleSubmit} className="p-4 bg-light rounded shadow mb-5">
        <input className="form-control mb-2" placeholder="Donor Name" value={form.donorName} onChange={e=>setForm({...form,donorName:e.target.value})} required />
        <input className="form-control mb-2" placeholder="Food Type" value={form.foodType} onChange={e=>setForm({...form,foodType:e.target.value})} required />
        <input className="form-control mb-2" placeholder="Quantity" value={form.quantity} onChange={e=>setForm({...form,quantity:e.target.value})} required />
        <input className="form-control mb-2" placeholder="Location" value={form.location} onChange={e=>setForm({...form,location:e.target.value})} required />
        <Button type="submit" variant="success" className="w-100">Donate</Button>
      </form>

      <h3 className="text-center mb-4">📋 Recent Donations</h3>
      <div className="d-flex flex-wrap gap-3 justify-content-center">
        {donations.length>0?donations.map(d=>(
          <Card key={d._id} style={{width:"18rem"}} className="shadow">
            <Card.Body>
              <Card.Title>{d.donorName}</Card.Title>
              <Card.Text>{d.quantity} of {d.foodType}<br />📍 {d.location}<br />Status: {d.status||"Pending"}</Card.Text>
            </Card.Body>
          </Card>
        )):<p className="text-muted">No donations yet.</p>}
      </div>
    </Container>
  );
}

// -------------------- REQUEST FOR FOOD --------------------
function Request() {
  const [requests, setRequests] = useState([]);
  const [formData, setFormData] = useState({ name: "", address: "", foodType: "", quantity: "", mobile: "" });

  useEffect(()=>{ fetchRequests(); }, []);

  const fetchRequests = async () => {
    try { const res = await axios.get("http://localhost:5000/requests"); setRequests(res.data); }
    catch(err){ console.error(err); }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/requests", formData);
      setFormData({ name: "", address: "", foodType: "", quantity: "", mobile: "" });
      fetchRequests();
    } catch(err){ console.error(err); }
  };

  const handleChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  return (
    <Container className="mt-5">
      <h2 className="text-center mb-4">Request for Food</h2>
      <form onSubmit={handleSubmit} className="p-4 bg-light rounded shadow mb-5">
        <input name="name" className="form-control mb-2" placeholder="Name" value={formData.name} onChange={handleChange} required/>
        <input name="address" className="form-control mb-2" placeholder="Address" value={formData.address} onChange={handleChange} required/>
        <input name="foodType" className="form-control mb-2" placeholder="Food Type" value={formData.foodType} onChange={handleChange} required/>
        <input name="quantity" className="form-control mb-2" placeholder="Quantity" value={formData.quantity} onChange={handleChange} required/>
        <input name="mobile" className="form-control mb-2" placeholder="Mobile" value={formData.mobile} onChange={handleChange} required/>
        <Button type="submit" variant="primary" className="w-100">Submit Request</Button>
      </form>

      <h3 className="text-center mb-4">📋 Recent Requests</h3>
      <div className="d-flex flex-wrap gap-3 justify-content-center">
        {requests.length>0?requests.map(r=>(
          <Card key={r._id} style={{width:"18rem"}} className="shadow">
            <Card.Body>
              <Card.Title>{r.name}</Card.Title>
              <Card.Text>{r.quantity} of {r.foodType}<br/>📍 {r.address}<br/>📞 {r.mobile}<br/>Status: {r.status||"Pending"}</Card.Text>
            </Card.Body>
          </Card>
        )):<p className="text-muted">No requests yet.</p>}
      </div>
    </Container>
  );
}

// -------------------- CONTRIBUTORS --------------------
function Contributors() {
  const contributors = [
    { name:"Alice", role:"NGO Volunteer", img:"/images/ngo.jpg" },
    { name:"Bob", role:"Orphanage Helper", img:"/images/orphan.jpg" },
    { name:"Charlie", role:"Community Support", img:"/images/community.jpeg" }
  ];

  return (
    <Container className="mt-5">
      <h2 className="text-center mb-4">Contributors</h2>
      <Row className="g-4">
        {contributors.map((c,i)=>(
          <Col md={4} key={i}>
            <Card className="h-100 shadow">
              <Card.Img variant="top" src={c.img}/>
              <Card.Body>
                <Card.Title>{c.name}</Card.Title>
                <Card.Text>{c.role}</Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

// -------------------- CONTACT US --------------------
function Contact() {
  const [formData,setFormData]=useState({name:"",email:"",message:""});

  const handleChange=e=>setFormData({...formData,[e.target.name]:e.target.value});

  const handleSubmit=async e=>{
    e.preventDefault();
    try{
      await axios.post("http://localhost:5000/contacts", formData);
      alert("Message sent!");
      setFormData({name:"",email:"",message:""});
    }catch(err){ console.error(err); }
  };

  return (
    <Container className="mt-5">
      <h2 className="text-center mb-4">Contact Us</h2>
      <form onSubmit={handleSubmit} className="p-4 bg-light rounded shadow">
        <input name="name" className="form-control mb-2" placeholder="Name" value={formData.name} onChange={handleChange} required/>
        <input name="email" className="form-control mb-2" placeholder="Email" value={formData.email} onChange={handleChange} required/>
        <textarea name="message" className="form-control mb-2" placeholder="Message" rows="4" value={formData.message} onChange={handleChange} required/>
        <Button type="submit" variant="success" className="w-100">Send Message</Button>
      </form>
    </Container>
  );
}

// -------------------- MAIN APP --------------------
function App() {
  return (
    <Router>
      <Navbar bg="dark" variant="dark" expand="lg" sticky="top">
        <Container>
          <Navbar.Brand as={Link} to="/">🍴 FoodSaver</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav"/>
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link as={Link} to="/">Home</Nav.Link>
              <Nav.Link as={Link} to="/donate">Donate</Nav.Link>
              <Nav.Link as={Link} to="/request">Request Food</Nav.Link>
              <Nav.Link as={Link} to="/contributors">Contributors</Nav.Link>
              <Nav.Link as={Link} to="/contact">Contact Us</Nav.Link>
            </Nav>
            <Button variant="outline-light" className="me-2">Profile</Button>
            <Button variant="danger">Logout</Button>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/donate" element={<Donate />} />
        <Route path="/request" element={<Request />} />
        <Route path="/contributors" element={<Contributors />} />
        <Route path="/contact" element={<Contact />} />
      </Routes>
    </Router>
  );
}

export default App;
