import React, { useState, useEffect } from "react";
import axios from "axios";
import { Navbar, Nav, Container, Button, Carousel, Card, Row, Col } from "react-bootstrap";
import Donate from "./pages/Donate";

<Route path="/donate" element={<Donate />} />

function App() {
  const [donations, setDonations] = useState([]);
  const [form, setForm] = useState({
    donorName: "",
    foodType: "",
    quantity: "",
    location: "",
  });

  useEffect(() => {
    fetchDonations();
  }, []);

  const fetchDonations = async () => {
    try {
      const res = await axios.get("http://localhost:5000/donations");
      setDonations(res.data);
    } catch (err) {
      console.error("Error fetching donations:", err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/donations", form);
      setForm({ donorName: "", foodType: "", quantity: "", location: "" });
      fetchDonations();
    } catch (err) {
      console.error("Error submitting donation:", err);
    }
  };

  return (
    <>
      {/* Navbar */}
      <Navbar bg="dark" variant="dark" expand="lg" sticky="top">
        <Container>
          <Navbar.Brand href="#">🍴 FoodSaver</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="#">Home</Nav.Link>
              <Nav.Link href="#donate">Donate</Nav.Link>
              <Nav.Link href="#request">Request For Food</Nav.Link>
              <Nav.Link href="#contributors">Contributors</Nav.Link>
              <Nav.Link href="#contact">Contact Us</Nav.Link>
            </Nav>
            <Button variant="outline-light" className="me-2">
              Profile
            </Button>
            <Button variant="danger">Logout</Button>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {/* HOME PAGE IMAGE (Your Uploaded Banner) */}
      <div>
        <img
          src="/main.jpg" // <-- place your image inside public/ folder with this exact name
          alt="Main Banner"
          className="w-100"
          style={{ maxHeight: "450px", objectFit: "cover" }}
        />
      </div>

      {/* 3 Blog Divisions */}
      <Container className="my-5">
        <Row className="text-center">
          <Col md={4}>
            <Card className="shadow border-0">
              <Card.Img
                variant="top"
                src="https://source.unsplash.com/400x250/?donation,food"
              />
              <Card.Body>
                <Card.Title>Fight Hunger</Card.Title>
                <Card.Text>
                  Join us in making sure no food goes to waste while people go
                  hungry.
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>

          <Col md={4}>
            <Card className="shadow border-0">
              <Card.Img
                variant="top"
                src="https://source.unsplash.com/400x250/?volunteer,charity"
              />
              <Card.Body>
                <Card.Title>Be a Volunteer</Card.Title>
                <Card.Text>
                  Contribute your time to help distribute meals to the needy.
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>

          <Col md={4}>
            <Card className="shadow border-0">
              <Card.Img
                variant="top"
                src="https://source.unsplash.com/400x250/?community,help"
              />
              <Card.Body>
                <Card.Title>Build Community</Card.Title>
                <Card.Text>
                  Together, we can create a supportive network to end hunger.
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>

      {/* Donation Form */}
      <Container className="mt-5" id="donate">
        <h2 className="text-center mb-4">🍲 Donate Food</h2>
        <form onSubmit={handleSubmit} className="p-4 bg-light rounded shadow">
          <input
            className="form-control mb-2"
            placeholder="Donor Name"
            value={form.donorName}
            onChange={(e) => setForm({ ...form, donorName: e.target.value })}
          />
          <input
            className="form-control mb-2"
            placeholder="Food Type"
            value={form.foodType}
            onChange={(e) => setForm({ ...form, foodType: e.target.value })}
          />
          <input
            className="form-control mb-2"
            placeholder="Quantity"
            value={form.quantity}
            onChange={(e) => setForm({ ...form, quantity: e.target.value })}
          />
          <input
            className="form-control mb-2"
            placeholder="Location"
            value={form.location}
            onChange={(e) => setForm({ ...form, location: e.target.value })}
          />
          <Button type="submit" variant="success" className="w-100">
            Donate
          </Button>
        </form>
      </Container>

      {/* Display Donations */}
      <Container className="mt-5">
        <h3 className="text-center mb-4">📋 Recent Donations</h3>
        <div className="d-flex flex-wrap gap-3 justify-content-center">
          {donations.length > 0 ? (
            donations.map((d) => (
              <Card key={d._id} style={{ width: "18rem" }} className="shadow">
                <Card.Body>
                  <Card.Title>{d.donorName}</Card.Title>
                  <Card.Text>
                    {d.quantity} of {d.foodType}
                    <br />
                    📍 {d.location}
                    <br />
                    Status: {d.status || "Pending"}
                  </Card.Text>
                </Card.Body>
              </Card>
            ))
          ) : (
            <p className="text-muted">No donations yet.</p>
          )}
        </div>
      </Container>
    </>
  );
}

export default App;
