import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [donations, setDonations] = useState([]);
  const [form, setForm] = useState({ donorName: "", foodType: "", quantity: "", location: "" });

  useEffect(() => {
    axios.get("http://localhost:5000/donations").then(res => setDonations(res.data));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:5000/donations", form);
    setForm({ donorName: "", foodType: "", quantity: "", location: "" });
    const res = await axios.get("http://localhost:5000/donations");
    setDonations(res.data);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>üç≤ Food Donation App</h2>
      <form onSubmit={handleSubmit}>
        <input placeholder="Donor Name" value={form.donorName} onChange={e => setForm({ ...form, donorName: e.target.value })} />
        <input placeholder="Food Type" value={form.foodType} onChange={e => setForm({ ...form, foodType: e.target.value })} />
        <input placeholder="Quantity" value={form.quantity} onChange={e => setForm({ ...form, quantity: e.target.value })} />
        <input placeholder="Location" value={form.location} onChange={e => setForm({ ...form, location: e.target.value })} />
        <button type="submit">Donate</button>
      </form>

      <h3>üìã Donations List</h3>
      <ul>
        {donations.map(d => (
          <li key={d._id}>
            {d.donorName} donated {d.quantity} of {d.foodType} at {d.location} (Status: {d.status})
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
